    private void login() {
        
        Conexion conn = null;
        Connection con = null;
        //get the login information from user:
        String username = username_textfield.getText().trim();
        String user_password = String.valueOf(password_field.getPassword());
        String hashed_user_password;
        PreparedStatement preparedStatement = null;
        ResultSet result_set = null;
        
        
 //===================-Preparing DB connection======================//
        try{

            conn = new Conexion();
            con = conn.getConexion();
            

            String sql = "SELECT pass_word FROM com_gmbh WHERE username = ?";
            
            preparedStatement  = con.prepareStatement(sql);

            preparedStatement.setString(1, username);
            result_set = preparedStatement.executeQuery();

            if (!result_set.isBeforeFirst()) {
                System.out.println("User ist nicht vorhanden");
                message.setText("Benutzername ist nicht vorhanden.");
                //todo: add an alert window.
            } else {
                while (result_set.next()) {
                    String retrieved_password = result_set.getString("pass_word");
                    H512 hashed = new H512();
                    hashed_user_password = hashed.encrypter(user_password);

                    if (retrieved_password.equals(hashed_user_password)) {
                        message.setText("Willkommen");

                        //todo: go to next window. -----------****************ADD !!!
                        new Dashboard().setVisible(true);
                        this.setVisible(false);

                    } else {
                        message.setText("Falsche Anmeldedaten!");
                        //todo: add an alert window.
                    }
                }
            }

        } catch (SQLException e) {
            e.printStackTrace();
            System.out.println(e.getMessage());
        } finally {
            if (result_set != null) {
                try {
                    result_set.close();
                } catch (SQLException e) {
                    e.printStackTrace();
                    System.out.println(e.getMessage());
                }
            }
            if (conn != null) {
               conn.close();
            }

        }
    }
            
            
        
        
        

